import React, { useState } from 'react';
import './Chatbox.css';

const Chatbox = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const toggleChatbox = () => {
    setIsOpen(!isOpen);
  };

  const generateBotResponse = (userMessage) => {
    const msg = userMessage.toLowerCase();

    if (msg.includes('oi') || msg.includes('olÃ¡')) {
      return 'OlÃ¡! Como posso te ajudar? ðŸ˜Š';
    } else if (msg.includes('horÃ¡rio')) {
      return 'Nosso horÃ¡rio de atendimento Ã© das 8h Ã s 18h de segunda a sexta.';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else if (msg.includes('preÃ§o')) {
      return 'Os preÃ§os variam dependendo do produto. Qual produto vocÃª deseja saber mais?';
    } else {
      return 'Desculpe, ainda estou aprendendo... Pode reformular sua pergunta?';
    }
  };

  const handleSend = () => {
    if (newMessage.trim() !== '') {
      const userMessage = newMessage;
      const botResponse = generateBotResponse(userMessage);

      setMessages([...messages, { sender: 'user', text: userMessage }, { sender: 'bot', text: botResponse }]);
      setNewMessage('');
    }
  };

  return (
    <div className="chatbox-container">
      <button onClick={toggleChatbox} className="chatbox-toggle">
        {isOpen ? 'Fechar Chat' : 'Abrir Chat'}
      </button>

      {isOpen && (
        <div className="chatbox">
          <div className="chatbox-messages">
            {messages.map((msg, index) => (
              <div key={index} className={`chatbox-message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>
          <div className="chatbox-input">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Digite sua mensagem..."
              onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            />
            <button onClick={handleSend}>Enviar</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chatbox;
